mapper.select:
SELECT price
FROM inventory_sizes
WHERE size_id = ${sizeId}

select COUNT(*) > 0
FROM cart_item
WHERE size_id= #{sizeId} AND user_id = #{userId}

mapper.update:
update cart_item
SET quantity = quantity + #{quantity} , updated_at = NOW()
WHERE item_id = #{itemId} AND size_id = #{sizeId}
